package main

import (
	"fmt"
	"math"
	"strings"
)

func minWindow(str1, str2 string) string {
	sizeStr1, sizeStr2 := len(str1), len(str2)

	minSubLen := math.Inf(1)

	indexS1, indexS2 := 0, 0
	minSubsequence := ""

	for indexS1 < sizeStr1 {

		if str1[indexS1] == str2[indexS2] {

			indexS2++

			if indexS2 == sizeStr2 {
				start, end := indexS1, indexS1
				indexS2--
				// decrement pointer indexS2 and start a reverse loop

				for indexS2 >= 0 {
					if str1[start] == str2[indexS2] {
						indexS2--
					}

					start--
				}

				start++

				if float64(end-start+1) < minSubLen {
					minSubLen = float64(end - start + 1)
					minSubsequence = str1[start : end+1]
				}

				indexS1 = start
				indexS2 = 0
			}
		}
		indexS1++
	}
	return minSubsequence

}

func main() {

	str1 := []string{"abcdebdde", "fgrqsqsnodwmxzkzxwqegkndaa", "zxcvnhss", "alpha", "beta"}
	str2 := []string{"bde", "kzed", "css", "la", "ab"}

	str1 = append(str1, "abcdedeaqdweq")
	str2 = append(str2, "aqeq")

	testCase := strings.Repeat("f", 500000) + "eeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee "

	str1 = append(str1, testCase)
	str2 = append(str2, "fffessa")

	for i := 0; i < len(str1); i++ {
		fmt.Printf("%d. \tInput string: (%s, %s)\n", i+1, str1[i], str2[i])
		fmt.Printf("\tSubsequence string: %s\n", minWindow(str1[i], str2[i]))
		fmt.Printf("%s\n", strings.Repeat("-", 100))
	}

}
